import {useQuery} from "react-query";
import styled from "styled-components";
import {motion, AnimatePresence} from "framer-motion";
import {getMovies, IGetMoviesResult} from "../api";
import {makeImagePath} from "../utilities";

const Wrapper = styled.div`
  background-color: black;

`;

const Loader = styled.div`
  height: 20vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Banner = styled.div<{ bgPhoto: string; }>`
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 60px;
  background-image: linear-gradient(rgb(0, 0, 0, 0.5), rgb(0, 0, 0, 0)) , url(${(props) => props.bgPhoto});
  background-size: cover;
`;

const Title = styled.h2`
  font-size: 68px;
`;

const Overview = styled.p`
    font-size: 30px;
    width: 50%;
    margin-bottom: 20px;
`;

const Slider = styled.div`
  position: relative;
`;

const Row = styled(motion.div)`
  display: grid;
  gap: 10px;
  grid-template-columns: repeat(6, 1fr);
  position: absolute;
  width: 100%;
`;

const Box = styled(motion.div)`
  background-color: white;
  height: 200px;
`;

function Home() {
  const {data, isLoading} = useQuery<IGetMoviesResult>(
    ["movies", "nowPlaying"],
    getMovies
  );
  return (
    <Wrapper>{isLoading ? (
      <Loader>로딩중...</Loader>
    ) : (
      <>
        <Banner bgPhoto={makeImagePath(data?.results[6].backdrop_path || "")}>
            <Title>{data?.results[6].title}</Title>
            <Overview>{data?.results[6].overview}</Overview>
        </Banner>
        <Slider>
            <AnimatePresence>
              <Row>
                <Box />
                <Box />
                <Box />
                <Box />
                <Box />
                <Box />
              </Row>
            </AnimatePresence>
        </Slider>
      </>
    )}
    </Wrapper>
  );
}

export default Home;
