import {useQuery} from "react-query";
import styled from "styled-components";
import {motion, AnimatePresence} from "framer-motion";
import {getMovies, IGetMoviesResult} from "../api";
import {makeImagePath} from "../utilities";
import {useState} from "react";

const Wrapper = styled.div`
  background-color: black;

`;

const Loader = styled.div`
  height: 20vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Banner = styled.div<{ bgPhoto: string; }>`
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 60px;
  background-image: linear-gradient(rgb(0, 0, 0, 0.5), rgb(0, 0, 0, 0)) , url(${(props) => props.bgPhoto});
  background-size: cover;
`;

const Title = styled.h2`
  font-size: 68px;
`;

const Overview = styled.p`
    font-size: 30px;
    width: 50%;
    margin-bottom: 20px;
`;

const Slider = styled.div`
  position: relative;
  top: -100px;
`;

const Row = styled(motion.div)`
  display: grid;
  gap: 5px;
  grid-template-columns: repeat(6, 1fr);
  position: absolute;
  width: 100%;
`;

const Box = styled(motion.div)<{ bgPhoto: string;}>`
  background-color: white;
  background-image: url(${props => props.bgPhoto});
  height: 200px;
  font-size: 66px;
`;

const rowVariants = {
  hidden: {
    x: window.outerWidth + 5,
  },
  visible: {
    x: 0,
  },
  exit: {
    x: -window.outerWidth - 10,
  },
}

const offset = 6;

function Home() {
  const {data, isLoading} = useQuery<IGetMoviesResult>(
    ["movies", "nowPlaying"],
    getMovies
  );

  const [index, setIndex] = useState(0);
  const [leaving, setLeaving] = useState(false);
  const incraseIndex = () => {
    if (data) {
      if (leaving) return;
      toggleLeaving();
      const totalMovies = data?.results.length - 1;
      const maxIndex = Math.ceil(totalMovies / offset) - 1;
      setIndex((prev) => prev === maxIndex ? 0 : prev + 1);
    }
  };
  const toggleLeaving = () => setLeaving((prev) => !prev);

  return (
    <Wrapper>{isLoading ? (
      <Loader>로딩중...</Loader>
    ) : (
      <>
        <Banner
          onClick={incraseIndex}
          bgPhoto={makeImagePath(data?.results[6].backdrop_path || "")}
        >
          <Title>{data?.results[6].title}</Title>
          <Overview>{data?.results[6].overview}</Overview>
        </Banner>
        <Slider>
          <AnimatePresence initial={false} onExitComplete={toggleLeaving}>
            <Row
              variants={rowVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
              transition={{type: "tween", duration: 1}}
              key={index}
            >
              {data?.results.slice(1).slice(offset * index, offset * index + offset)
                .map((movie) => (
                  <Box
                    key={movie.id}
                    bgPhoto={makeImagePath(movie.backdrop_path)}
                  />
                ))}
            </Row>
          </AnimatePresence>
        </Slider>
      </>
    )}
    </Wrapper>
  );
}

export default Home;
