import {IToDo} from "../atoms";

function ToDo({ text, category }: IToDo) {
  const onClick = (e:React.MouseEvent<HTMLButtonElement>) => {
    console.log('i wana go to ', e.currentTarget.name);
  };
  return (
      <li>
        <span>{text}</span>
        {category !== "DOING" && (
            <button name="DOING" onClick={onClick}>하기</button>
        )}

        {category !== "TO_DO" && (
            <button name="TO_DO" onClick={onClick}>할 일</button>
        )}

        {category !== "DONE" && (
            <button name="DONE" onClick={onClick}>완료</button>
        )}
      </li>
  );
}

export default ToDo;
